static void imageIter( const Plane<uint8_t>& in, Plane<uint8_t>& out, const int* lut )
{
  int width  = in.getCols() - 1 ;
  int height = in.getRows() - 1 ;

  for( int y = 1; y < height; ++y )
  {
    for( int x = 1 ; x < width; ++x )
    {
      if( in.at(x,y) == 0 )
      {
              out.at(x,y) = 0;
      }
      else
      {
        int ind =
            ( in.at(x-1,y-1) == 255 )     + ( in.at(x,y-1) == 255 ) *  8 + ( in.at(x+1,y-1) == 255 ) *  64 +
            ( in.at(x-1,y  ) == 255 ) * 2 + ( in.at(x,y  ) == 255 ) * 16 + ( in.at(x+1,y  ) == 255 ) * 128 +
            ( in.at(x-1,y+1) == 255 ) * 4 + ( in.at(x,y+1) == 255 ) * 32 + ( in.at(x+1,y+1) == 255 ) * 256 ;

        out.at(x,y) = lut[ind];
      }
    }
  }
}

